import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { Container, Box, Typography, TextField, Button, Alert } from '@mui/material';
import { styled } from '@mui/system';

const Logo = styled('img')({
  maxWidth: '150px',
  marginBottom: '1.5rem',
  display: 'block',
  marginLeft: 'auto',
  marginRight: 'auto',
});

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!email || !password) {
      setError('Please fill in both email and password.');
      return;
    }
    setLoading(true);
    try {
      // Effettua la richiesta di login
      const response = await axios.post(`${process.env.REACT_APP_AUTH_API_URL}/auth/login`, {
        email,
        password,
      });
      const token = response.data.access_token;
      localStorage.setItem('access_token', token);

      // Recupera i dettagli dell'utente (endpoint /users/me)
      const userResponse = await axios.get(`${process.env.REACT_APP_USER_API_URL}/users/me`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const userData = userResponse.data;
      
      // Se il faculty_id non è impostato, reindirizza alla pagina di selezione della facoltà
      if (!userData.faculty_id) {
        navigate('/faculty-selection');
      } else {
        navigate('/dashboard');
      }
    } catch (err) {
      setError(err.response?.data?.detail || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="xs" sx={{ mt: 8 }}>
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          padding: 3,
          boxShadow: 3,
          borderRadius: 2,
          backgroundColor: '#FFFFFF',
        }}
      >
        <Logo src="/logo.png" alt="SapienzaAdvisor Logo" />
        <Typography component="h1" variant="h5" sx={{ color: '#0D47A1', mb: 2 }}>
          SapienzaAdvisor Login
        </Typography>
        {error && (
          <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
            {error}
          </Alert>
        )}
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1, width: '100%' }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            autoFocus
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            disabled={loading}
            sx={{ mt: 3, mb: 2, backgroundColor: '#0D47A1' }}
          >
            {loading ? 'Logging in...' : 'Login'}
          </Button>
          <Typography variant="body2" align="center">
            New account?{' '}
            <Link to="/register" style={{ textDecoration: 'none', color: '#0D47A1' }}>
              Register
            </Link>
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default LoginPage;
